query writer ($id: ID!, $page: Int) {
  writer: writer (id: $id) {
    title
    belongsTo (page: $page, perPage: 3) @paginate {
      totalCount
      pageInfo {
        totalPages
        currentPage
      }
      edges {
        node {
          ...on Post {
            title
            timeToRead
    	      date (format: "MMMM D, YYYY")
            path
            summary
            writers {
              title
            }
          }
        }
      }
    }
  }
}
