result:
query cat ($id: ID!, $page: Int) {
  cat: cat (id: $id) {
    title
    belongsTo (page: $page, perPage: 3) @paginate {
      totalCount
      pageInfo {
        totalPages
        currentPage
      }
      edges {
        node {
          ...on Post {
            title
            date (format: "MMMM D, YYYY")
            summary
            timeToRead
            path
            cover
            writers {
              title
              path
            }
            cats {
              title
              path
            }
          }
        }
      }
    }
  }
}
